mapper.insert:
INSERT INTO BOARD (
      MEM_ID
    , BOARD_TYPE
    , BOARD_TITLE
    , BOARD_CONTENT
) VALUES (
      #{mem_id}
    , #{board_type}
    , #{board_title}
    , #{board_content}
)

mapper.update:
UPDATE SET BOARD
      BOARD_TYPE = #{board_type}
    , BOARD_TITLE = #{board_title}
    , BOARD_CONTENT = #{board_content}
    , BOARD_MOD = #{board_mod}
    WHERE BOARD_ID = #{board_id}
