mapper.insert:
INSERT INTO BOARD (
      MEM_ID
    , BOARD_TYPE
    , BOARD_TITLE
    , BOARD_CONTENT
) VALUES (
      #{mem_id}
    , #{board_type}
    , #{board_title}
    , #{board_content}
)

mapper.update:
UPDATE BOARD
    SET
        BOARD_TYPE = #{board_type}
      , BOARD_TITLE = #{board_title}
      , BOARD_CONTENT = #{board_content}
      , BOARD_MOD = #{board_mod}
    WHERE BOARD_ID = #{board_id}

mapper.insert:
INSERT INTO BOARD_LIKE(
    BOARD_ID, MEM_ID
) VALUES (
    #{board_id}, #{mem_id}
)

mapper.delete:
DELETE FROM BOARD_BOOKMARK
    WHERE BOARD_ID = #{board_id}
      AND MEM_ID = #{mem_id}

mapper.insert:
INSERT INTO BOARD_BOOKMARK(
    BOARD_ID, MEM_ID
) VALUES (
             #{board_id}, #{mem_id}
         )

mapper.delete:
DELETE FROM BOARD_BOOKMARK
    WHERE BOARD_ID = #{board_id}
      AND MEM_ID = #{mem_id}

